import ReducerBuilder from './reducer-builder.js';
import ValueReducer from './reducers/value-reducer.js';
import EventReducer from './reducers/event-reducer.js';
import AnyEventReducer from './reducers/any-event-reducer.js';
import StructureReducer from './reducers/structure-reducer.js';
import LinkedListReducer from './reducers/linked-list-reducer.js';
import DictionaryObjectReducer from './reducers/dictionary-object-reducer.js';
import NeverReducer from './reducers/never-reducer.js';
import _Option from './option.js';
import _LinkedList from './linked-list.js';

/**
 * Represents an optional type, i.e., some value or none.
 * @type {Option<T>}
 */
export const Option = _Option;

/**
 * Represents a linked-list
 * @type {LinkedList<T>}
 */
export const LinkedList = _LinkedList;

/**
 * A factory for creating reducer queries.
 */
export default class Reduce {
  /**
   * Creates a reducer query over events in scope matching the event type specified.
   * @param {string} eventType - The event type
   * @returns {ReducerBuilder} The query over the matching events
   */
  static eventsOfType(eventType) {
    return new ReducerBuilder(new EventReducer(eventType));
  }

  /**
   * Creates a reducer query of over all events in scope.
   * @returns {ReducerBuilder} The query over the events in scope
   */
  static allEvents() {
    return new ReducerBuilder(new AnyEventReducer());
  }

  /**
   * A reducer query returning the value provided.
   * @param value - The value
   * @returns {ReducerBuilder} - The reducer query containing the value
   */
  static value(value) {
    return new ReducerBuilder(new ValueReducer(value));
  }

  /**
   * Builds a reducer query from a structure of reducer queries and constants representing
   * the reduced structure.
   * @returns {ReducerBuilder} - The reducer query builder
   */
  static structure(reducerStructure) {
    return new ReducerBuilder(new StructureReducer(reducerStructure));
  }

  /**
   * @typedef {Object} CollectionConfiguration
   * @property {ReducerBuilder<TArgs>|ReducerBuilder<Array<TArgs>>} additions - The reducer that produces argument objects (single or arrays) to be constructed into items for the collection
   * @property {?ReducerBuilder<TKey>|ReducerBuilder<Array<TKey>>} removals - The reducer that produces keys of items to be removed from the collction
   * @property {function(args:TArgs):TItem|function(args:TArgs):ReducerBuilder<TItem>} itemFactory - The function that builds new items from arguments produced by the additions reducer. The items can be constant values or ReducerBuilders.
   * @property {function(item:TItem):TKey} itemKey - The function that extracts a key from an existing item
   */

  /**
   * Builds a reducer query of a linked list from a reducer for additions, a reducer for removals, a function to construct
   * items and a function to extract the key from a constructed item. Keys are used for removal and addressing the auxillary
   * state to ensure accumulators are seeded correctly etc.
   * @param  {CollectionConfiguration} configuration - the configuration needed to build the list
   * @returns {ReducerBuilder} - The reducer query builder
   */
  static linkedListOf(configuration) {
    return new ReducerBuilder(new LinkedListReducer(configuration));
  }

  /**
   * Builds a reducer query of a dictionary object from a reducer for additions, a reducer for removals, a function to construct
   * items and a function to extract the key from a constructed item. Keys are used for removal and addressing the auxillary
   * state to ensure accumulators are seeded correctly etc.
   * @param  {CollectionConfiguration} configuration - the configuration needed to build the list
   * @returns {ReducerBuilder} - The reducer query builder
   */
  static dictionaryObjectOf(configuration) {
    return new ReducerBuilder(new DictionaryObjectReducer(configuration));
  }

  /**
   * A reducer query that never produces any updates in state.
   * @returns {ReducerBuilder} - The reducer query builder
   */
  static never() {
    return new ReducerBuilder(new NeverReducer());
  }
}

/**
 * Represents a linked-list
 */
export default class LinkedList {
  /**
   * Returns the empty list.
   * @return {LinkedList<T>} The empty list
   */
  static nil() {
    return null;
  }

  /**
   * Returns a new list of the concatenation of a head value to another list (the tail). 
   * @param  {T} head - The value to add
   * @param  {LinkedList<T>} tail - The tail list
   * @return {LinkedList<T>} A linked list containing the head and the tail.
   */
  static cons(head, tail) {
    return { head, tail };
  }

  /**
   * Returns true if the list passed is empty.
   * @param  {LinkedList<T>} list - The list to test
   * @return {Boolean} Whether the list is empty
   */
  static isEmpty(list) {
    return list === null || list === undefined;
  }

  /**
   * Produces a list containing elements from the list provided in reversed order.
   * @param  {LinkedList<T>} list - The list to reverse
   * @return {LinkedList<T>} The reversed list
   */
  static reverse(list) {
    return LinkedList.reduce(list, (acc, x) => LinkedList.cons(x, acc), LinkedList.nil());
  }

  /**
   * Produces a new list containing only those elements from the list provided that pass the predicate function.
   * @param  {LinkedList<T>} list - The list to filter
   * @param  {function(x:T):boolean} predicate - The predicate used to test for inclusion in the new list
   * @return {LinkedList<T>} The filtered list
   */
  static filter(list, predicate) {
    return LinkedList.reverse(LinkedList.reduce(list, (acc, x) => predicate(x) ? LinkedList.cons(x, acc) : acc, LinkedList.nil()));
  }

  /**
   * Produces a new list of elements that are the result of mapping elements from an existing list using the selector
   * function provided.
   * @param  {LinkedList<T1>} list - The list to map
   * @param  {function(x:T1):T2} selector - The mapping function
   * @return {LinkedList<T2>} The mapped list.
   */
  static map(list, selector) {
    return LinkedList.reverse(LinkedList.reduce(list, (acc, x) => LinkedList.cons(selector(x), acc), LinkedList.nil()));
  }

  /**
   * Iterates over a list calling the action on each element in the list.
   * @param  {LinkedList<T>} list - The list to iterate
   * @param  {function(x:T)} action - The action to call for each element
   */
  static forEach(list, action) {
    LinkedList.reduce((_, x) => action(x));
  }

  /**
   * Creates an array containing the same elements as the list.
   * @param  {LinkedList<T>}  list - The list to create an array from
   * @param  {Boolean} [isReversedOrder=true] - Whether the elements are stored in reverse order
   * @return {Array<T>} The resulting array
   */
  static toArray(list, isReversedOrder = true) {
    const result = LinkedList.reduce(list, (acc, x) => {
      acc.push(x);
      return acc;
    }, []);
    isReversedOrder && result.reverse();
    return result;
  }

  /**
   * Creates a linked list containing the same elements as the array.
   * @param  {Array<T>}  array - The array to create a list from
   * @param  {Boolean} [isReversedOrder=true] - Whether the elements should be stored in reverse order in the list
   * @return {LinkedList<T>} The resulting list
   */
  static ofArray(array, isReversedOrder = true) {
    const reduce = isReversedOrder ? array.reduce.bind(array) : array.reduceRight.bind(array);
    return reduce((acc, x) => LinkedList.cons(x, acc), LinkedList.nil());
  }

  /**
   * Reduces a value by folding over the elements in a linked list from head to tail.
   * @param  {LinkedList<T>} list - The list to fold over
   * @param  {function(acc:TAcc, x:T):TAcc} accumulate - The function to accumulate a value
   * @param  {TAcc} seed - The initial value for the accumulator
   * @return {TAcc} The final accumulator
   */
  static reduce(list, accumulate, seed) {
    let current = list;
    let acc = seed;
    while (!LinkedList.isEmpty(current)) {
      acc = accumulate(acc, current.head);
      current = current.tail;
    }
    return acc;
  }
}

let none;

/**
 * Represents an optional type, i.e., some value or none.
 */
export default class Option {
  /**
   * @private
   */
  constructor(hasValue, value) {
    this._value = value;
    /**
     * Returns true if the option wraps a value, i.e., if it is some
     * @type {Boolean}
     */
    this.isSome = hasValue;
    /**
     * Returns true if the option doesn't wrap a value, i.e., if it is none
     * @type {Boolean}
     */
    this.isNone = !this.isSome;
  }

  /**
   * Returns none.
   * @return {[type]} [description]
   */
  static none() {
    return none || (none = new Option(false));
  }

  /**
   * Returns the value wrapped as a some option. 
   * @param  {T} value - The value the wrap
   * @return {Option<T>} The value wrapped in an option
   */
  static some(value) {
    return new Option(true, value);
  }

  /**
   * Returns the value if the option is a some otherwise throws.
   * @return {T} The value.
   */
  get() {
    if (this.isNone) throw new Error('Expected some');
    return this._value;
  }

  /**
   * Maps the value wrapped in the option if any.
   * @param  {function(x:T1):T2} selector - The mapping function
   * @return {Option<T2>} The mapped option
   */
  map(selector) {
    return this.isNone ? this : Option.some(selector(this._value));
  }

  /**
   * Filters the value wrapped in the option if any. If it passes the predicate retains some otherwise none.
   * @param  {function(x:T):boolean} predicate - The filter function
   * @return {Option<T>} The filtered option
   */
  filter(predicate) {
    return this.isSome && predicate(this._value) ? this : Option.none(); 
  }

  /**
   * Returns the value wrapped in the option if some otherwise the other value passed as an argument.
   * @param  {T} otherValue - The alternate value to return
   * @return {T} The wrapped value if some else otherValue
   */
  otherwise(otherValue) {
    return this.isSome ? this._value : otherValue;
  }

  /**
   * Returns the seed if none otherwise accumulates a new value using the seed and the wrapped value using the
   * accumulator function.
   * @param  {function(acc:TAcc, x:T)} accumulator - The function to accumulate a result if some
   * @param  {TAcc} seed - The value to return if none wrapped
   * @return {TAcc} The accumulated value or the seed if none
   */
  reduce(accumulator, seed) {
    return this.isSome ? accumulator(seed, this._value) : seed;
  }

  /**
   * Returns none if none otherwise the option produced by projecting the wrapped value onto another option using
   * the selector function.
   * @param  {function(x:T1):Option<T2>} selector - The projection function
   * @return {Option<T2>} The resulting option.
   */
  bind(selector) {
    return this.isSome ? selector(this._value) : Option.none();
  }
}

import MappedReducer from './reducers/mapped-reducer.js';
import FlatReducedReducer from './reducers/flat-reduced-reducer.js';
import FilteredReducer from './reducers/filtered-reducer.js';
import ScopedReducer from './reducers/scoped-reducer.js';
import FoldingReducer from './reducers/folding-reducer.js';
import ReducerQuery from './reducer-query.js';
import LinkedList from './linked-list.js';

/**
 * A fluent reducer-query builder.
 */
export default class ReducerBuilder {

  /** @ignore */
  constructor(parent) {
    /** 
     * The previous reducer in the fluent chain.
     * @type {Reducer}
     * @private
     */
    this._parent = parent;
  }

  /**
   * Maps the reduced value onto another value using a function.
   * @param {function(x:T1):T2} selector - The mapping function
   * @returns {ReducerBuilder<T2>} A reducer builder that maps the value
   */
  select(selector) {
    return this.map(selector);
  }

  /**
   * Maps the reduced value onto another value using a function.
   * @param {function(x:T1):T2} selector - The mapping function
   * @returns {ReducerBuilder<T2>} A reducer builder that maps the value
   */
  map(selector) {
    return this._wrap(new MappedReducer(this._parent, selector));
  }


  /**
   * Maps a value onto a reducer and produces values from that producer until the next value.
   * @param  {function(x:T2):ReducerBuilder<T2>} reducerSelector - The function to project a value onto a reducer builder.
   * @return {ReducerBuilder<T2>} A reducer builder
   */
  flatReduce(reducerSelector) {
    return this._wrap(new FlatReducedReducer(this._parent, reducerSelector));
  }

  /**
   * Filters the value by applying a predicate function to the value
   * @param {function(x:T):boolean} predicate - The predicate function
   * @returns {ReducerBuilder<T>} A reducer builder that filters using the predicate
   */
  where(predicate) {
    return this.filter(predicate);
  }

  /**
   * Filters the value by applying a predicate function to the value
   * @param {function(x:T):boolean} predicate - The predicate function
   * @returns {ReducerBuilder<T>} A reducer builder that filters using the predicate
   */
  filter(predicate) {
    return this._wrap(new FilteredReducer(this._parent, predicate));
  }

  /**
   * Scopes the events that are visible to reducers in the fluent chain leading to this.
   * @param  {function(e:Event):boolean} predicate - The predicate to determine if an event is in scope.
   * @returns {ReducerBuilder<T>} A reducer builder that is scoped to events passing the predicate.
   */
  scoped(predicate) {
    return this._wrap(new ScopedReducer(this._parent, predicate));
  }

  /**
   * Accumulates the sum of the reduced values.
   * @param {number|string} zeroValue - The zero value for the addition operator.
   * @returns {ReducerBuilder<T>} A reducer builder that accumulates the sum
   */
  sum(zeroValue) {
    return this._wrap(new FoldingReducer(this._parent, (acc, x) => acc + x, zeroValue));
  }

  /**
   * Accumulates using the accumulate function over the reduced values.
   * @param {function(acc:T2, value:T1): T2} accumulate - The accumulator function
   * @param seedValue - The seed value for the accumulation.
   * @returns {ReducerBuilder<T2>} A reducer builder that accumulates the sum
   */
  fold(accumulate, seedValue) {
    return this._wrap(new FoldingReducer(this._parent, accumulate, seedValue));
  }

  /**
   * Constructs a reducer from the query defined using the builder.
   * @returns {ReducerQuery} a reducer query
   */
  build() {
    return new ReducerQuery(this._parent);
  }

  /**
   * <b>INTERNAL</b>
   * Returns the most recent reducer in the fluent chain.
   * @return {Reducer} The most recent reducer in the fluent chain.
   */
  unwrap() {
    return this._parent;
  }

  /** ignore */
  _wrap(reducer) {
    return new ReducerBuilder(reducer);
  }
}

import {VALUE} from './reducers/storage-keys.js';
import Option from './option.js';

/**
 * <b>INTERNAL</b>
 * Provides context for executing reducers so they can store and retrieve state.
 */
export default class ReducerContext {
  /**
   * Constructs a context used to by a tree of reducers.
   * @param {Object} - The previous auxillary state used to retrieve stored values.
   * @param {Array<Event>} - The events to reduce.
   */
  constructor(previousAuxillary, eventMaybe) {
    /**
     * This is the current level of scope. I.e., the route from the root to the current reducer.
     * @type {Array<string>}
     */
    this._route = [];
    /**
     * This is the previous auxillary state used to retrieve stored values.
     * @type {Object}
     */
    this._previousAuxillary = previousAuxillary || {};

    /**
     * This contains the auxillary state built up in a reduction pass. For example, this
     * may include accumulator values for folds that cannot easily be derived from looking 
     * at the previous state.
     * 
     * @type {Object}
     *
     * @example <caption>Illustration where previous state isn't the same as the accumulator</caption>
     * From.events().ofAnyType().select(_ => 1).sum(0).select(x => 2 * x)
     */
    this.nextAuxillary = {};

    this._event = eventMaybe;
  }

  /**
   * Returns an option wrapping the event being processed or none if there is none or it is out of scope.
   * @return {Option<Event>} The event or none
   */
  getEvent() {
    return this._event;
  }

  /**
   * Reduces the value of a child reducer and uses the storage key provided to address it in auxillary state.
   * @param  {string} storageKey - The address to use for the child in auxillary state
   * @param  {Reducer} reducer - The child reducer
   * @return {Option<T>} The value if any produced in this context by the child reducer  
   */
  getValue(storageKey, reducer) {
    return this._reduce(storageKey, reducer);
  }

  /**
   * Reduces the value of a child reducer and uses the storage key provided to address it in auxillary state.
   * Doesn't use previous auxillary state to derive the value (i.e., it is "fresh").
   * @param  {string} storageKey - The address to use for the child in auxillary state
   * @param  {Reducer} reducer - The child reducer
   * @return {Option<T>} The value if any produced in this context by the child reducer  
   */
  getFreshValue(storageKey, reducer) {
    return this._reduce(storageKey, reducer, true);
  }

  /**
   * Stores a value in auxillary storage under the storage key.
   * @param  {string} storageKey - The address to use to access the value in auxillary state.
   * @param  value - The <i>serializable</i> value to store.
   */
  storeValue(storageKey, value) {
    this.nextAuxillary[storageKey] = value;
  }

  /**
   * Retrieves a value stored in the previous auxillary storage on the last reduction.
   * @param  {string} storageKey - The address to use to access the value in auxillary state.
   * @return  The value
   */
  getStoredValue(storageKey) {
    return this._previousAuxillary[storageKey];
  }

  /**
   * Returns the last value reduced by the current reducer if no storage key is provided otherwise that of
   * the child with the address given by the storage key. Looks up this value in auxillary state.
   * @param  {?string} storageKey - The address of the child or omitted for own value.
   * @return {T} The last value reduced by the reducer
   */
  getPreviousReduction(storageKey = null) {
    let previousEntry;
    if (storageKey) {
      previousEntry = 
        this._previousAuxillary && 
        this._previousAuxillary[storageKey] && 
        this._previousAuxillary[storageKey][VALUE];
    } else {
      previousEntry = this._previousAuxillary && this._previousAuxillary[VALUE];
    }
    return previousEntry && previousEntry.length ? Option.some(previousEntry[0]) : Option.none();
  }

  /**
   * Scopes the context within the action callback to only hold events matching the predicate.
   * @param  {function(e:Event):boolean} predicate - The filter over the events
   * @param  {function():T} action - The callback
   * @return {T} the value produced by the callback
   */
  scopedBy(predicate, action) {
    const isEventInScope = this._event.reduce((_, e) => predicate(e), true);
    let result;
    if (isEventInScope) {
      result = action(); 
    } else {
      // TODO: could optimise this when we already have a value and aren't seeding by copying the existing state.
      const event = this._event;
      this._event = Option.none();
      result = action();
      this._event = event;
    }
    return result;
  }

  _reduce(storageKey, reducer, shouldUseFreshState = false) {
    const parentNextAux = this.nextAuxillary;
    const parentPrevAux = this._previousAuxillary;
    this.nextAuxillary = parentNextAux[storageKey] || (parentNextAux[storageKey] = {});
    this._previousAuxillary = !shouldUseFreshState && this._previousAuxillary && this._previousAuxillary[storageKey];
    const newValue = reducer.reduce(this);
    // TODO: Should protect against this being something non-serializable. I.e., a function or a reducer!
    if (newValue.isSome) {
      this.nextAuxillary[VALUE] = [newValue.get()];
    } else if (this._previousAuxillary && this._previousAuxillary[VALUE] && this._previousAuxillary[VALUE].length) {
      this.nextAuxillary[VALUE] = this._previousAuxillary[VALUE];
    }
    this.nextAuxillary = parentNextAux;
    this._previousAuxillary = parentPrevAux;
    return newValue;
  }
}

import ReducerContext from './reducer-context.js';
import {ROOT} from './reducers/storage-keys.js';
import Option from './option.js';

/**
 * An entry-point to a tree of reducers forming a reducer query. Provides convenience methods for reducing events.
 */
export default class ReducerQuery {
  /**
   * <b>INTERNAL</b>
   * Constructs a reducer query from a reducer.
   * @param {Reducer} - The root reducer to wrap.
   * @access internal
   */
  constructor(reducer) {
    this._reducer = reducer;
    this._eventIndex = 0;
  }

  /**
   * @typedef {Object} PreviousState
   * @property {Object} previousState - The last produced state.
   * @property {Object} previousAuxillary - The last auxillary state that contains accumulator values etc.
   * @property {?Array<Event>} events - The events to reduce (should only set one of `events` or `event` but not both)
   * @property {?Event} events - The event to reduce (should only set one of `events` or `event` but not both)
   */

  /**
   * @typedef {Object} NewState
   * @property {Object} newState - The newly produced state or the previous state if unchanged.
   * @property {Object} newAuxillary - The newly produced auxillary state that contains accumulator values etc.
   */

  /**
   * Reduces new events and the previous state into a new state.
   * @param  {?PreviousState} context - The last produced state if any.
   * @return {NewState} - The newly produced state.
   */
  reduce(context) {
    const events = context && (context.events || [context.event]) || [];
    const eventsMaybe = events.length ? events.map(Option.some) : [Option.none()];
    const output = eventsMaybe.reduce((acc, e) => {
      const internalContext = new ReducerContext(acc.auxillary, e);
      const updateMaybe = internalContext.getValue(ROOT, this._reducer);
      const update = updateMaybe.isSome ? updateMaybe : acc.update;
      return {auxillary: internalContext.nextAuxillary, update};
    }, {auxillary: context && context.previousAuxillary, update: Option.none()});

    const newAuxillary = output.auxillary;
    const newState = output.update.otherwise(context && context.previousState);
    return {newState, newAuxillary};
  }

  /**
   * Returns a store.
   * @param  {?NewState} previousState - The last produced state by another store if any.
   * @return {Object} A store
   * @property {function(e:Event)} dispatch - Dispatches an event into the store to update the state.
   * @property {function():T} getState - Returns the current state.
   */
  toStore(previousState) {
    let state = previousState || reduce();
    return {
      dispatch: event => state = reduce({previousAuxillary: context.newAuxillary, previousState: context.newState, event}),
      getState: () => state.newState
    };
  }
}

import Reducer from './reducer.js';
import Option from '../option.js';

/**
 * <b>INTERNAL</b>
 * Yields any events in the context.
 */
export default class AnyEventReducer extends Reducer {
  /** @ignore */
  reduce(context) {
    return context.getEvent();
  }
}

import Reducer from './reducer.js';
import Option from '../option.js';
import NeverReducer from './never-reducer.js';
import {ADDITION_SOURCE, REMOVAL_SOURCE, ARGS_PREFIX} from './storage-keys.js';

/**
 * Returns the single object wrapped in an array or the array if the argument is an array.
 * @param {T|Array<T>} itemOrArray - The item or array of items
 * @return {Array<T>} An array of items
 */
const asArray = itemOrArray =>
  itemOrArray instanceof Array ? itemOrArray : [itemOrArray];

/**
 * <b>INTERNAL</b>
 * A reducer of a collection that may itself contain reducers.
 */
export default class CollectionReducer extends Reducer {

  /**
   * Builds a reducer that creates of a collection from a reducer for additions, a reducer for removals, a function to construct
   * items and a function to extract the key from a constructed item. Keys are used for removal and addressing the auxillary
   * state to ensure accumulators are seeded correctly etc.
   * @param  {CollectionConfiguration} configuration - the configuration needed to build the collection
   */
  constructor(collectionApi, configuration) {
    super();
    const {additions, itemFactory, itemKey} = configuration;
    const removals = configuration.removals && configuration.removals.unwrap() || new NeverReducer();
    this._additionSource = additions.unwrap();
    this._removalSource = removals;
    this._keySelector = itemKey;
    this._elementSelector = itemFactory;
    this._collectionApi = collectionApi;
  }

  /** @ignore */
  reduce(context) {
    const previous = context.getPreviousReduction();
    let hasChanged = previous.isNone;
    let changeTracker = { hasChanged: previous.isNone };
    const existingItems = previous.otherwise(this._collectionApi.empty());
    let items = this._collectionApi.prepare(existingItems);
    // REMOVE
    items = this._filterRemovals(context, items, changeTracker)
    // UPDATE REMAINING
    items = this._updateRemaining(context, items, changeTracker);
    // ADD + SEED NEW
    items = this._consAdditions(context, items, changeTracker);
    return changeTracker.hasChanged ? Option.some(items) : Option.none();
  }

  _filterRemovals(context, items, changeTracker) {
    const removals = context.getValue(REMOVAL_SOURCE, this._removalSource);
    if (removals.isSome) {
      changeTracker.hasChanged = true;
      const removedKeys = asArray(removals.get());
      return this._collectionApi.remove(items, this._keySelector, removedKeys);
    }
    return items;
  }

  _updateRemaining(context, items, changeTracker) {
    return this._collectionApi.map(items, x => {
      const key = this._keySelector(x);
      const reducerArgs = context.getStoredValue(ARGS_PREFIX + key);
      context.storeValue(ARGS_PREFIX + key, reducerArgs); // Keep for next time.
      const reducer = this._elementSelector(reducerArgs);
      let newValue = x;
      // If it is a reducer we reduce the new value.
      if (reducer.unwrap) {
        const reducedValue = context.getValue(key, reducer.unwrap());
        changeTracker.hasChanged = changeTracker.hasChanged || reducedValue.isSome;
        newValue = reducedValue.otherwise(x);
      }
      return newValue;
    });
  }

  _consAdditions(context, items, changeTracker) {
    const additions = context.getValue(ADDITION_SOURCE, this._additionSource);
    if (additions.isSome) {
      changeTracker.hasChanged = true;
      return asArray(additions.get()).reduce((acc, reducerArgs) => {
        const reducerOrValue = this._elementSelector(reducerArgs);
        let value;
        let key; 
        if (reducerOrValue.unwrap) {
          const reducedValue = context.getFreshValue('temp', reducerOrValue.unwrap());
          value = reducedValue.get();
          key = this._keySelector(value);
          // Move storage to under actual key. TODO: refactor this.
          context.getValue(key, {reduce: () => reducedValue});
        } else {
          value = reducerOrValue;
          key = this._keySelector(value);
        }
        context.storeValue(ARGS_PREFIX + key, reducerArgs); // Keep for next time.
        return this._collectionApi.put(acc, key, value);
      }, items);
    }
    return items;
  }
}

