/**
 * <b>INTERNAL</b>
 * Yields the provided value <i>once</i>.
 */
export default class ValueReducer extends Reducer {
  /**
   * Constructs a reducer that yields the provided value <i>once</i>.
   * @param value - The value to produce
   */
  constructor(value) {
    super();
